import React, { useState, useEffect } from 'react';
import './Home.css';
import Button from '../components/Button/Button';

// Import ảnh từ thư mục assets
import slide1 from '../components/Assets/slide.jpg';
import slide2 from '../components/Assets/slide2.jpg';
import slide3 from '../components/Assets/slide3.jpg';
import children from '../components/Assets/children.jpeg';

const Home = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const slides = [slide1, slide2, slide3];

  // Tự động chuyển slide mỗi 4 giây
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 4000);
    return () => clearInterval(interval);
  }, [slides.length]);

  // Hàm chuyển slide thủ công
  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const campaigns = [
    { id: 1, title: 'Hỗ trợ trẻ em mồ côi', raised: '50,000,000 VNĐ', goal: '100,000,000 VNĐ', image: "children", description: 'Giúp trẻ em có một tương lai tươi sáng.' },
    { id: 2, title: 'Xây cầu cho vùng sâu', raised: '120,000,000 VNĐ', goal: '200,000,000 VNĐ', image: 'https://via.placeholder.com/400x300', description: 'Kết nối cộng đồng qua những cây cầu.' },
    { id: 3, title: 'Học bổng cho học sinh nghèo', raised: '30,000,000 VNĐ', goal: '50,000,000 VNĐ', image: 'https://via.placeholder.com/400x300', description: 'Mang tri thức đến mọi nhà.' },
  ];

  return (
    <div className="homepage-main">
      {/* Banner */}
      <section className="banner">
        <div className="banner-wrapper">
          {slides.map((slide, index) => (
            <div
              key={index}
              className={`banner-slide ${index === currentSlide ? 'active' : ''}`}
              style={{ backgroundImage: `url(${slide})` }}
            ></div>
          ))}
          <div className="banner-overlay">
            <h1>Lan tỏa yêu thương, kết nối cộng đồng</h1>
            <p>Mỗi đóng góp của bạn là một tia hy vọng cho những hoàn cảnh khó khăn.</p>
            <button className="banner-btn">Khám phá chiến dịch</button>
          </div>
          {/* Nút điều hướng */}
          <button className="nav-btn prev-btn" onClick={prevSlide}>
            &#10094; {/* Mũi tên trái */}
          </button>
          <button className="nav-btn next-btn" onClick={nextSlide}>
            &#10095; {/* Mũi tên phải */}
          </button>
        </div>
        <div className="slide-indicators">
          {slides.map((_, index) => (
            <span
              key={index}
              className={`indicator ${currentSlide === index ? 'active' : ''}`}
              onClick={() => setCurrentSlide(index)}
            ></span>
          ))}
        </div>
      </section>

      {/* Campaigns */}
      <section className="campaigns">
        <h2>Những chiến dịch cần bạn</h2>
        <div className="campaign-grid">
          {campaigns.map((campaign) => (
            <div key={campaign.id} className="campaign-item">
              <div className="campaign-image">
                <img src={campaign.image} alt={campaign.title} />
                <div className="campaign-hover">
                  <button className="hover-btn">Đóng góp ngay</button>
                </div>
              </div>
              <div className="campaign-info">
                <h3>{campaign.title}</h3>
                <p className="description">{campaign.description}</p>
                <div className="funding-info">
                  <p>Đã quyên góp: <span>{campaign.raised}</span></p>
                  <p>Mục tiêu: <span>{campaign.goal}</span></p>
                </div>
                <div className="progress-container">
                  <div
                    className="progress-fill"
                    style={{
                      width: `${(parseInt(campaign.raised.replace(/[^0-9]/g, '')) / parseInt(campaign.goal.replace(/[^0-9]/g, ''))) * 100}%`,                    }}
                  ></div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
      <section className="hero-section">
        <div className="hero-content">
          <h1>Cùng nhau tạo nên sự khác biệt</h1>
          <p>
            Mỗi đóng góp của bạn giúp chúng tôi mang hy vọng đến những người cần nó nhất. 
            Hãy chung tay xây dựng một thế giới tốt đẹp hơn!
          </p>
          <button className="hero-button">Quyên góp ngay</button>
        </div>
      </section>
    
    </div>
  );
};

export default Home;
