import { Link } from "react-router-dom";
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth } from '../../firebase';
import { signOut } from 'firebase/auth';
import './Navbar.css'
import Button from "../Button/Button";
import AuthPage from "../AuthPage/AuthPage";



const Navbar = () => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();
  
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      setUser(currentUser);
    });
    return () => unsubscribe();
  }, []);

  const handleLogout = async () => {
    await signOut(auth);
  };
  return (
    <div className="navbar">
      <h1 className="logo">HopeHouse</h1>
      <div className="nav-links">
        <Link to="/">Trang chủ</Link>
        <Link to="/projects">Dự án</Link>
        <Link to="/verify">Xác minh</Link>
        <Link to="/guide">Hướng dẫn</Link>
      </div>
      <div className="navbar-actions">
        {user ? (
          <>
            <span>Welcome, {user.email}</span>
            <Button onClick={handleLogout}>Log Out</Button>
          </>
        ) : (
          <>
            <button onClick={() => navigate('/auth?mode=login')}>Log In</button>
            <button onClick={() => navigate('/auth?mode=signup')}>Sign Up</button>
          </>
        )}
      </div>
    </div>
  );
};

// <Button text="Đăng nhập" onClick={() => navigate('/auth?mode=login')} />
//         <Button text="Đăng ký" onClick={() => navigate('/auth?mode=signup')} />

export default Navbar
