import React from "react";

const Home = () => {
  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-blue-600 text-white p-6 text-center text-2xl font-bold">
        Dự án quyên góp xây trường vùng xa
      </header>

      {/* Hero Section */}
      <section className="flex flex-col items-center text-center p-10 bg-white shadow-lg">
        <h1 className="text-4xl font-bold text-gray-800">Hãy cùng chung tay xây dựng tương lai</h1>
        <p className="mt-4 text-lg text-gray-600">Mỗi đóng góp của bạn sẽ giúp mang lại môi trường học tập tốt hơn cho trẻ em vùng xa.</p>
        <button className="mt-6 px-6 py-3 bg-blue-600 text-white text-lg font-semibold rounded-lg hover:bg-blue-700">
          Quyên góp ngay
        </button>
      </section>

      {/* Progress Section */}
      <section className="p-8 text-center">
        <h2 className="text-2xl font-bold text-gray-700">Tiến độ gây quỹ</h2>
        <div className="w-3/4 mx-auto bg-gray-200 rounded-full h-6 mt-4 relative">
          <div className="bg-green-500 h-6 rounded-full text-white text-sm text-center leading-6" style={{ width: "65%" }}>
            65% Đã đạt được
          </div>
        </div>
      </section>

      {/* Featured Projects */}
      <section className="p-8">
        <h2 className="text-2xl font-bold text-gray-700 text-center">Dự án nổi bật</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mt-6">
          {[1, 2, 3].map((project) => (
            <div key={project} className="bg-white p-6 shadow-md rounded-lg">
              <h3 className="text-lg font-bold text-gray-800">Dự án {project}</h3>
              <p className="text-gray-600 mt-2">Mô tả ngắn về dự án...</p>
              <button className="mt-4 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700">
                Chi tiết
              </button>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
