import React from 'react';
import './Home.css';

const Home = () => {
  const campaigns = [
    { id: 1, title: 'Hỗ trợ trẻ em mồ côi', raised: '50,000,000 VNĐ', goal: '100,000,000 VNĐ', image: 'https://via.placeholder.com/300x200' },
    { id: 2, title: 'Xây cầu cho vùng sâu', raised: '120,000,000 VNĐ', goal: '200,000,000 VNĐ', image: 'https://via.placeholder.com/300x200' },
    { id: 3, title: 'Học bổng cho học sinh nghèo', raised: '30,000,000 VNĐ', goal: '50,000,000 VNĐ', image: 'https://via.placeholder.com/300x200' },
  ];

  return (
    <div className="homepage">
      {/* Header */}


      {/* Banner */}
      <section className="banner">
        <div className="banner-content">
          <h1>Chung tay vì một cộng đồng tốt đẹp hơn</h1>
          <p>Hỗ trợ những hoàn cảnh khó khăn với sự đóng góp của bạn.</p>
          <button className="banner-btn">Tham gia ngay</button>
        </div>
      </section>

      {/* Campaigns Section */}
      <section className="campaigns">
        <h2>Chiến dịch nổi bật</h2>
        <div className="campaign-list">
          {campaigns.map((campaign) => (
            <div key={campaign.id} className="campaign-card">
              <img src={campaign.image} alt={campaign.title} />
              <h3>{campaign.title}</h3>
              <p>Đã quyên góp: {campaign.raised}</p>
              <p>Mục tiêu: {campaign.goal}</p>
              <div className="progress-bar">
                <div
                  className="progress"
                  style={{
                    width: `${(parseInt(campaign.raised.replace(/[^0-9]/g, '')) / parseInt(campaign.goal.replace(/[^0-9]/g, ''))) * 100}%`,
                  }}
                ></div>
              </div>
              <button className="campaign-btn">Đóng góp</button>
            </div>
          ))}
        </div>
      </section>

      {/* Footer */}
      <footer className="footer">
        <div className="footer-content">
          <p>&copy; 2025 CharityHub. Mọi quyền được bảo lưu.</p>
          <div className="social-links">
            <a href="#">Facebook</a>
            <a href="#">Twitter</a>
            <a href="#">Instagram</a>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Home;
